    public static boolean match(String str, int index){
        if(str.charAt(index) == '('){
            match(str, index+1);
        }else{
            if(index-1 < 0)
                return false;
            if(str.charAt(str.length-1-index) == '(' &&  str.charAt(index) != ')')
                return false;
        }
        return true;
    }
