    public static int gcd(int a, int b){
        if(a == 0 && b > 0)
            return b;
        else if( a > 0 && b == 0){
            return a;
        }else{
            if(a >= b){
                return gcd(a-b, b);
            }else{
                return gcd(a, b-a);
            }
        }
    }
