    // Input: 1->2->3->5->4->6  (5->4) reverse
    // output:1->2->3->4->5->6
    public static void Fix(Node head)
    {
        Stack<Integer> stack = new Stack<Integer>();
        Node curr = head;

        Node minNode = new Node(Integer.MIN_VALUE);
        minNode.next = curr;
        curr = head = minNode;
        while(curr != null && curr.next != null)
        {
            curr = curr.next; 
        }
        Node prev = curr;
        curr.next = new Node(Integer.MAX_VALUE);
        Node start = null;
        curr = head;
        while(curr != null)
        {
            Node next = curr.next;
            if(next != null)
            {
                if(curr.data > next.data)
                {
                    stack.push(curr.data);
                    if(start == null)
                        start = curr;
                }
                else if(start != null)
                {
                    stack.push(curr.data);
                    break;
                }
            }
            curr = next;
        }
        while(start != null && !stack.empty())
        {
            start.data = stack.pop();
            System.out.println("start.data["+start.data+"]");
            start = start.next;
        }
        head = head.next;
        prev.next = null;

    }

