class Interval implements Comparable<Interval> {
    public int begin;
    public int end;
    public Interval(int begin, int end) {
        this.begin = begin;
        this.end = end;
    }
    public int compareTo(Interval inter) {
        if(this.begin > inter.begin)
            return 1;
        else if(this.begin == inter.begin)
            return 0;
        else
            return -1;
    }
    public String toString() {
        return "[" + begin + " " + end + "]";
    }
}

