class CircularLinkedList
{
    Node first;
    public CircularLinkedList()
    {
        first = null;
    }
    public void print()
    {
        Node tmpFirst = first;
        if( tmpFirst != null)
        {
            Node curr = tmpFirst.next;
            System.out.println("["+tmpFirst.data+"]");
            while(tmpFirst != curr)
            {
                System.out.println("["+curr.data+"]");
                curr = curr.next;
            }
        }
    }
    public void print(Node first)
    {
        this.first = first;
        print();
    }
    public void setFirst(Node first)
    {
        this.first = first;
    }
    public void insert(Node node)
    {
        if(node != null)
        {
            if(first == null) //zero node
            {
                first = node;
                node.next = first;
            }
            else
            {
                Node curr = first;
                Node next = curr.next;
                if(next == null) //one node
                {
                    first.next = node;
                    node.next = first;
                }
                else //two or more nodes
                {
                    boolean done = false;
                    while(!done)
                    {
                        if(curr.data < next.data)
                        {
                           if(curr.data < node.data && node.data < next.data) 
                           {
                                curr.next = node;
                                node.next = next;
                                done = true;
                           }
                        }
                        else
                        {
                            if(node.data > curr.data || node.data < next.data)
                            {
                                curr.next = node;
                                node.next = next;
                                done = true;
                            }
                        }
                        curr = curr.next;
                        next = next.next;
                    }
                }
            }
        }
    }
}
