class Entry{
    public String key;
    public Object value;
    public Entry next;
    public Entry(String key, Object value){
        this.key = key;
        this.value = value;
    }
}

class Hash{
    public Entry[] arr;
    public int max;
    public int count = 0;
    public Hash(int max){
        this.max = max;
        arr = new Entry[max];
    }

    public Node get(String key){
        int hash = key.hashCode() % max;
        return (Node)arr[hash].value;
    }
    public void delete(String key){
        if(key != null){
            int hash = key.hashCode() % max;
            Entry curr = arr[hash];
            if(curr != null){
                Entry prev = null;
                while(curr != null){
                    if(curr.key == key)
                        break;
                    else{
                        prev = curr;
                        curr = curr.next;
                    }
                }
                if(prev == null){
                    arr[hash] = curr.next;
                    count--;
                }else{
                    if(curr != null){
                        prev.next = curr.next;
                        count--;
                    }
                }
            }
        }
    }
    public void put(String key, Node node){
        if(count < max){
            int hash = key.hashCode() % max;
            Entry curr = arr[hash];
            if(curr == null){
                arr[hash] = new Entry(key, node);
                count++;
            }
            else{
                Entry prev = null;
                while(curr != null){
                    if(curr.key.equals(key)){
                        // Replace with new value
                        curr.value = node;
                        break;
                    }
                    else{
                        prev = curr;
                        curr = curr.next;
                    }
                }
                // No key is found
                if(curr == null){
                    prev.next = new Entry(key, node);
                    count++;
                }
            }
        }
        
    }
}


