class SLinkedList
{
    Node head;
    public SLinkedList()
    {
        head = null;
    }
    public void addBST(Node root)
    {
        if(head == null)
            head = new Node(root);
        else
        {
            Node curr = head;
            while(curr.right != null)
            {
                curr = curr.right;
            }
            curr.right = new Node(root);
        }
    }
    public void add(int data)
    {
        if(head == null)
        {
            head = new Node(data);
        }
        else
        {
            Node curr = head;
            while(curr.right != null)
            {
                curr = curr.right; 
            }
            curr.right = new Node(data);
        }
    }
    public void show()
    {
        Node curr = head;
        while(curr != null)
        {
            System.out.print("["+curr.data+"]");
            curr = curr.right;
        }
    }
    public void inorder(Node r)
    {
        if( r != null)
        {
            inorder(r.left);
            System.out.print("["+r.data+"]");
            inorder(r.right);
        }
    }
    public void showBST()
    {
        Node curr = head;
        while(curr != null)
        {
            Node tmp = curr;
            inorder(tmp.root);    
            curr = curr.right;
            System.out.println("=========");
        }
    }
}

class BST
{
    Node root;
    public BST()
    {
        root = null;
    }
    public void insert(int data)
    {
        if(root == null)
        {
            root = new Node(data);
        }
        else
        {
            Node curr = root;
            boolean done = false;
            while(!done)
            {
                if(data < curr.data)
                {
                    if(curr.left != null)
                    {
                        curr = curr.left;
                    }
                    else
                    {
                        curr.left = new Node(data);
                        done = true;
                    }
                }    
                else
                {
                    if(curr.right != null)
                    {
                        curr = curr.right;
                    }
                    else
                    {
                        curr.right = new Node(data);
                        done = true;
                    }
                }
            }
        }
    }
}


