    //Given square matrix
    public static void printDiagonal(int[][] array) {
        if(array != null) {
            int len = array.length;
            for(int i=0; i<2*len-1; i++) {

                for(int j=0; j<len && j+i<len; j++) {
                    if(j == j+i) {
                        System.out.print("["+array[j][j]+"]");
                    }
                }
                if(i==0)
                    System.out.println("");
                for(int j=0; j<len && j+i < len; j++) {
                    if(j < j+i) {
                        System.out.print("["+array[j][j+i]+"]");
                    }
                }
                if(i != 0)
                    System.out.println("");
                for(int j=0; j<len && j+i<len; j++) {
                    if(j < j+i) {
                        System.out.print("["+array[j+i][j]+"]");
                    }
                }
                if(i != 0)
                    System.out.println("");
            }
        }
    }
