    public static void merge(int[] A, int lo, int mid, int hi)
    {
        if( A != null)
        {
            int[] B = new int[hi-lo+1];

            int index1 = lo;
            int index2 = mid+1;
            int k=0;
            while(k < (hi-lo+1))
            {
                if(index1 > mid)
                {
                    B[k] = A[index2];
                    index2++;
                }
                else if(index2 > hi)
                {
                    B[k] = A[index1];
                    index1++;
                }
                else
                {
                    if(A[index1] < A[index2])
                    {
                        B[k] = A[index1]; index1++;
                    }
                    else
                    {
                        B[k] = A[index2]; index2++;
                    }
                }
                k++;
            }
            for(int i=0; i<B.length; i++)
                A[lo+i] = B[i];
        }
    }
    public static void mergeSort(int[] A, int lo, int hi)
    {
        if(lo<hi)
        {
            int mid = (lo+hi)/2;
            mergeSort(A, lo, mid);
            mergeSort(A, mid+1, hi);
            merge(A, lo, mid, hi);
        }
    }
