class SLinkedList
{
    Node head;
    public SLinkedList()
    {
        this.head = null;
    }
    public void add(Node node)
    {
        if(this.head == null)
        {
            this.head = node;
        }
        else
        {
            Node curr = this.head;
            while(curr.next != null)
            {
                curr = curr.next;
            }
            curr.next = node; 
        }
    }
    public void show()
    {
        Node curr = this.head;
        while(curr != null)
        {
            System.out.println("["+curr.coeff+"]["+curr.power+"]");
            curr = curr.next;
        }
    }
}

