    public static List<Integer> nPrime(int n) {
        List<Integer> list = new ArrayList<Integer>();
        list.add(2);
        int count = 1;
        int num = 3;
        while(count <= n) {
            boolean isPrime = true;
            for(Integer p : list) {
                if(num % p == 0) {
                    isPrime = false;
                    break;
                }
            }
            if(isPrime) {
                list.add(num);
                count++;
            }
            num++;
        }
        return list;
    }
