    //Use Level order to write node to file
    public static void serializeLevel(Node root, BufferedWriter out1) {
        try {
            if(root != null) {
                Queue<Node> q1 = new LinkedList<Node>();
                Queue<Node> q2 = new LinkedList<Node>();
                q1.add(root);
                while(q1.peek() != null || q2.peek() != null) {
                    while(q1.peek() != null) {
                        String s = "";
                        Node node = q1.remove();
                        s += node.data + " ";
                        if(node.left != null) {
                            s += node.left.data + " ";
                            q2.add(node.left);
                        } else
                            s += "#" + " ";
                        if(node.right != null) {
                            s += node.right.data + " ";
                            q2.add(node.right);
                        } else
                            s += "#" + " ";

                        s += "\n";
                        out1.write(s);
                    }

                    while(q2.peek() != null) {
                        String s = "";
                        Node node = q2.remove();
                        s += node.data + " ";
                        if(node.left != null) {
                            s += node.left.data + " ";
                            q1.add(node.left);
                        } else
                            s += "#" + " ";
                        if(node.right != null) {
                            s += node.right.data + " ";
                            q1.add(node.right);
                        } else
                            s += "#" + " ";

                        s += "\n";
                        out1.write(s);
                    }
                }

            }
        } catch(Exception e) {
        }
    }

