    public static void serializeIndex(Node root, BufferedWriter out, int k) {
        if(root != null) {
            String s = k + ":" + root.data + "\n";
            try {
                serializeIndex(root.left, out, 2*k + 1);
                out.write(s);
                serializeIndex(root.right, out, 2*k + 2);
            } catch(Exception e) {
                System.err.println("Error" + e.getMessage());
            }
        }
    }
