    // deserialize with stack
    public static Node deserializeStack(Iterator<String> ite){
        Stack<Node> stack = new Stack<>(); 
        while(ite.hasNext()){
            String token = ite.next();
            if(!token.equals("#")){
                stack.push(new Node(token));
            }else{
                if(stack.size() > 1){
                    Node top = stack.pop();
                    Node peek = stack.peek();
                    peek.list.add(top);
                }
            }
        }
        return stack.peek();
    }
