    static Node mergeSortedList(Node head1, Node head2){
        if(head1 == null)
            return head2;
        if(head2 == null)
            return head1;
        Node curr1 = head1;
        Node curr2 = head2;
        Node curr = null; 
        Node head = null;

        while(curr1 != null || curr2 != null){
            if(curr1 == null){
                if(curr != null)
                    curr.next = new Node(curr2.data);
                else
                    head = curr = new Node(curr2.data);

                curr2 = curr2.next;

            }else if(curr2 == null){
                if(curr != null)
                    curr.next = new Node(curr1.data);
                else
                    head = curr = new Node(curr1.data);

                curr1 = curr1.next;
            }else{
                if(curr1.data < curr2.data){
                    if(curr == null){
                        head = curr = new Node(curr1.data);
                    }else{
                        curr.next = new Node(curr1.data);
                    }

                    curr1 = curr1.next;
                }else{
                    if(curr == null)
                        head = curr = new Node(curr2.data);
                    else{
                        curr.next = new Node(curr2.data);
                    }

                    curr2 = curr2.next;
                }
            }

            if(curr.next != null)
                curr = curr.next;
        }
        return head;
    }

    static Node insertNodeToSortedList(Node head, int num){
       Node curr = head; 
       Node prev = null;

       while(curr != null){
           if(curr.data < num){
               prev = curr;
               if(curr.next == null){
                   prev.next = new Node(num);
                   return head;
               }
           }else{
               if(prev != null){
                   prev.next = new Node(num);
                   prev.next.next = curr;
                   return head;
               }else{
                   // first node
                   Node node = new Node(num);
                   node.next = curr;
                   head = node;
                   return head;
               }
           }
           curr = curr.next;
       }
       return head;

    }

    static boolean isBalance(String str) {
        Stack<String> stack = new Stack<String>();
        if(str != null) {
            for(int i=0; i<str.length(); i++) {
                String s = str.charAt(i) + "";
                if(s.equals("(") || s.equals("[")) {
                    stack.push(s);
                } else {
                    if(s.equals(")")) {
                    if(!stack.empty() && stack.peek().equals("(")) {
                            stack.pop();
                        } else {
                            return false;
                        }
                    } else if(s.equals("]")) {
                    if(!stack.empty() && stack.peek().equals("[")) {
                            stack.pop();
                        } else {
                            return false;
                        }
                    } else {
                        System.out.println("Error");
                        return false;
                    }

                }
            }
        }
        return stack.empty();
    }
