    // clone a binary tree
    // Use postorder traversal
    public static Node CloneTree(Node root) {
        if(root != null) {
            Node left = CloneTree(root.left);
            Node right = CloneTree(root.right);
            Node parent = new Node(root.data);
            parent.left = left;
            parent.right = right;
            return parent;
        }
        return null;
    }

    public static void PostorderDFS(Node r) {
        if( r != null) {
            Stack<Node> st= new Stack<Node>();
            Map<Node, List<Node>> map = new HashMap<Node, List<Node>>();
            st.push(r);
            while(!st.empty()) {
                Node top = st.peek();
                if((top.left == null && top.right == null) || top.isVisited) {
                    Node node = st.pop();
                    System.out.println("[" + node.data + "]");

                } else if((top.left != null || top.right != null) && !top.isVisited) {
                    List<Node> list = new LinkedList<Node>();
                    if(!map.containsKey(top)) {
                        if(top.left != null)
                            list.add(top.left);
                        if(top.right != null)
                            list.add(top.right);
                        map.put(top, list);
                    } else {
                        list = map.get(top);
                    }
                    if(list.size() > 0) {
                        Node node = list.get(0);
                        st.push(node);
                        list.remove(0);
                        if(list.size() == 0) {
                            top.isVisited = true;
                            map.remove(top);
                        }
                    }
                }
            }
        }
    }
