		{
			char[] chArr =  str.toCharArray();	
			String s="";
			for(int i=0; i<chArr.length; i++)
			{
				if('0'<= chArr[i] && chArr[i] <= '9')
					 s += chArr[i];	
				else 
				{ if(!s.equals(""))
					{ list.add(s); s = "";}
					list.add(chArr[i]+"");
				}
			}
			if(!s.equals(""))
				list.add(s); s="";
		}
		for(int i=0; i<list.size(); i++)
		{
			System.out.println(list.get(i));
		}
		return list;
