        if(curr != null) {
            q.add(curr);
            while(q.size() > 0) {
                Node node = q.remove();
                Print.pbl("q.data=" + node.data);
                if(node.left != null)
                    q.add(node.left);
                if(node.right != null)
                    q.add(node.right);
            }
        }
